define(['core/notification','core/modal_factory'],function(Notification,ModalFactory){return{initialize:function(){let cacheTable=document.querySelectorAll('table.cachesused');if(cacheTable[0]){cacheTable[0].setAttribute('id','local-debugtoolbar-cache');}
if(cacheTable[1]){cacheTable[1].setAttribute('id','local-debugtoolbar-sessions');}
let modals=document.getElementsByClassName('local-debugtoolbar-modal');modals.forEach(function(modal){modal.addEventListener('click',function(e){let targetid=e.currentTarget.getAttribute('data-targetid');var targetElement=document.getElementById(targetid);ModalFactory.create({type:ModalFactory.types.ALERT,title:e.currentTarget.textContent,body:targetElement,large:true}).then(async function(modal){modal.show();return modal;}).catch(Notification.exception);});});}};});